    componentDidMount() {
        window.addEventListener('resize', this.resize);
        this.setState({
            ...this.state,
        }, () => {
            this.resize();
        })
        setInterval(e => {
            this.update()
        },
            60000
        )
    }

    render() {



        let params = {}
        let html = null;
        if (this.state.dimensions.height !== null) {
            let colour = "WHITE";
            let board = []
            for (const file of this.state.game.board) {
                let fileArr = [];
                for (const square of file) {
                    if (square !== null) {
                        params = {
                            handleClick: this.handleClick,
                            squareSize: Math.floor(this.state.dimensions.height / 8),
                            colour: colour,
                            ...square,
                        }
                    }
                    else {
                        params = {
                            handleClick: this.handleClick,
                            squareSize: Math.floor(this.state.dimensions.height / 8),
                            colour: colour,
                            type: null,
                            selected: false,
                            ...square
                        }
                    }
                    fileArr.push(
                        <tr>
                            <Piece params={params}></Piece>
                        </tr>
                    )

                    if (colour === 'WHITE') {
                        colour = 'BLACK'
                    } else {
                        colour = 'WHITE'
                    }
                }
                board.push(<td>
                    {fileArr}
                </td>)
                if (colour === 'WHITE') {
                    colour = 'BLACK'
                } else {
                    colour = 'WHITE'
                }
            }
            html = (
                <div className={styles.Board} id="Board">
                    {board}
                </div>
            )
        }
        else {
            html = (
                <div className={styles.Board} id="Board">
                </div>
            )
        }
        return html;
    }

    resize() {
        let div = document.getElementById("Board");
        if (div !== undefined) {
            this.setState({
                ...this.state,
                dimensions: {
                    width: div.clientWidth,
                    height: div.clientHeight,
                }
            })
        }
    }

    update() {
        if (this.props.app.gameId != undefined) {
            let params = {
                token: this.props.app.token,
                playerId: this.props.app.playerId,
                username: this.props.app.username,
                gameId: this.props.app.gameId
            }

            getMove(params)
                .then(response => {
                    let payload = {
                        ...response.game,
                    }
                    this.props.setBoard(payload);
                })
                .catch(error => {
                    console.log(error)
                })
        }
    }

