import { Redirect } from 'react-router-dom';
import React, { Component } from 'react';
import store from "../../Redux/store";
import { connect } from "react-redux";
import { setImplicitGrantCredentials } from "../../Redux/actions/action";


import styles from './Piece.module.css';



const mapStateToProps = state => {
    return {
        app: state,
    };
};

const mapDispatchToProps = state => {
    return {
        setImplicitGrantCredentials: props => store.dispatch(setImplicitGrantCredentials(props)),
        //setQueues: props => store.dispatch(setQueues(props))
        //  setUsers: props => store.dispatch(setUsers(props))
    };
};

class Piece extends Component {
    constructor(props) {
        super(props);
    }

    componentDidMount() {

    }

    render() {
        console.log(this.props.params);
        let html = null;
        if (this.props.params !== undefined) {
            let style = {
                width: this.props.params.squareSize + 'px',
                height: this.props.params.squareSize + 'px'
            }
            let image = (
                <img src='../Images/pawn_w.png'></img>
            )
            if (this.props.params.colour === "WHITE") {
                html = (
                    <div className={styles.Square_WHITE} style={style}>
                        <img src='../Images/pawn_w.png'></img>
                    </div>
                )
            }
            else {
                html = (
                    <div className={styles.Square_BLACK} style={style}>
                        <img src={"../Images/pawn_w.png"}></img>
                    </div>
                )
            }


        }
        else {
            html = (
                <div className={styles.Square_BLACK}>

                </div>
            )
        }
        return html;
    }

}

export default connect(mapStateToProps, mapDispatchToProps)(Piece);
