import {
  SET_IMPLICIT_GRANT_CREDENTIALS,
  FETCH_DOWNLOAD_START,
  //SET_QUEUE_LIST, SET_QUEUE
  SET_USER_LIST,
  SET_USER,
  SET_USER_CONVERSATIONS
} from "../constants/action-types";

export  function setImplicitGrantCredentials(payload) { //takes in the payload and dispatches it to the store.
    //debug("setImplicitGrantCredentials payload: %O", payload);
    return { type: SET_IMPLICIT_GRANT_CREDENTIALS, payload }
  };


export function setQueue(payload){ //this is no longer used.... there in case we decide to offer queue redirect options.
  //return { type : SET_QUEUE, payload}
}

export function setUsers(payload){//once list of users ahas been fetched. 
  return { type: SET_USER_LIST, payload};
}

export function setUser(payload){// set the user, this also triggers the getUserConversations 
  return { type: SET_USER, payload};
}
export function setUserConversations(payload){ //once the conversations have been fetched.
  return { type: SET_USER_CONVERSATIONS, payload};
}
