import logo from './logo.svg';
import './App.css';

import React, { Component } from 'react';
import store from "./Redux/store";
import { connect } from "react-redux";



const mapStateToProps = state => {
  return { store: state };
};

const mapDispatchToProps = state => {
  return {
    setCodeAuthorizationCredentials : props => store.dispatch(setCodeAuthorizationCredentials(props))
  };
};

class App extends Component {
  constructor(props) {
    super(props);
    console.log("constructor: props %O", this.props);
  }

  componentDidMount() {
    console.log("componentDidMount: props %O", this.props);

    /*if(this.props.store.app.implicitGrant.haveToken === false){
      let id  = this.props.store.app.implicitGrant.clientId;
