      console.log("id:%O url:%O", id,url);
      debug(`https://login.mypurecloud.ie/oauth/authorize?client_id=${id}&response_type=token&redirect_uri=${url}`);
      window.location.replace(`https://login.mypurecloud.ie/oauth/authorize?client_id=${id}&response_type=token&redirect_uri=${url}`);
      // this.props.history.push(`https://login.mypurecloud.ie/oauth/authorize?client_id=${id}&response_type=token&redirect_uri=${url}`);
    }*/
    if (this.props.store.app.codeAuthorization.haveToken === false) {
      let id = this.props.store.app.codeAuthorization.clientId;
      let url = this.props.store.app.codeAuthorization.callbackUrl;
      console.log("id: %O url:%O", id, url);
      console.log(`https://login.mypurecloud.ie/oauth/authorize?client_id=${id}&response_type=code&redirect_uri=${url}`);
      // this.props.setCodeAuthorizationCredentials(null);
      window.location.replace(`https://login.mypurecloud.ie/oauth/authorize?client_id=${id}&response_type=code&redirect_uri=${url}`);
      // this.props.history.push(`https://login.mypurecloud.ie/oauth/authorize?client_id=${id}&response_type=code&redirect_uri=${url}`);
    }

  }

  // <WrenAppBar appName={this.props.store.app.appName} appVersion={this.props.store.app.appVersion} isFetching={false} isLoggedIn={true} />
  // <WrenAppBar name={this.props.store.app.name}  version={this.props.store.app.version} isFetching={this.props.store.app.codeAuthorization.haveToken===false} isLoggedIn={this.props.store.app.codeAuthorization.haveToken}/>

  render() {
    console.log(this.props)
    if (this.props.loggedIn) {
      return (
        <div className="App">
          <header className="App-header">
            <img src={logo} className="App-logo" alt="logo" />
            <p>
              Edit <code>src/App.js</code> and save to reload.
            </p>
            <a
              className="App-link"
              href="https://reactjs.org"
              target="_blank"
              rel="noopener noreferrer"
            >
              Learn React
            </a>
          </header>
        </div>
      );
    }
    else {
      window.location("https://login.mypurecloud.ie/oauth/authorize/?client_id=4d125b8b-fb92-4a3c-989f-6edc7aff5a54&response_type=token&redirect_uri=https://localhost:3000/callback")
    }
  }
}

//export default App;
// export default App = connect(mapStateToProps, mapDispatchToProps)(withStyles(styles)(App));
//export default App = connect(mapStateToProps)(withStyles(styles)(App));
