import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
import { connect } from "react-redux";
//import './App.css';
import WrenAppBar from '../wrenAppBar/WrenAppBar';

import queryString from 'query-string'

import store from "../../redux/store";

import { setImplicitGrantCredentials } from "../../Redux/actions/general";

//import * as DEBUG from 'debug';
//const debug = new console.log("Wren:ImplicitGrant");

const mapStateToProps = state => {
  return {
    store: state,
  };
};

const mapDispatchToProps = state => {
  return {
    setImplicitGrantCredentials : props => store.dispatch(setImplicitGrantCredentials(props))
  };
};

class ImplicitGrant extends Component {
  constructor(props) {
    super(props);
    console.log("Constructor  props:%O", this.props);
  }

  componentDidMount(){
    console.log("DidMount  props: %O", this.props);
    const values = queryString.parse(this.props.location.hash);

    console.log("Parsed Query String", values);
    this.props.setImplicitGrantCredentials(values);

  }

  render() {
    console.log("Render  props: %O", this.props);

    let html = null;
    if(this.props.store.app.implicitGrant.haveToken){
      html =
      (
        <Redirect to="/" />
      )
    }
    else{
      html =
      (
        <div className="App">
          <WrenAppBar title={this.props.store.app.name}  isFetching={false} version={this.props.store.app.version}/>
        </div>
      )
    }

    return ( html );
  }
}

export default ImplicitGrant = connect(mapStateToProps, mapDispatchToProps)(ImplicitGrant);
