import logo from './logo.svg';
//import './App.css';
import { Redirect } from 'react-router-dom';
import React, { Component } from 'react';
import store from "./Redux/store";
import { connect } from "react-redux";
import Board from './Components/Board/Board';
import ControlBar from './Components/ControlBar/ControlBar';
import { setImplicitGrantCredentials } from "./Redux/actions/action";

import Login from './Components/Login/Login'

import styles from './App.module.css'


const mapStateToProps = state => {
  return {
    app: state,
  };
};

const mapDispatchToProps = state => {
  return {
    setImplicitGrantCredentials: props => store.dispatch(setImplicitGrantCredentials(props)),
    //setQueues: props => store.dispatch(setQueues(props))
    //  setUsers: props => store.dispatch(setUsers(props))
  };
};

class App extends Component {
  constructor(props) {
    super(props);
  }

  componentDidMount() {




  }

  render() {
    let html = null;
    if (this.props.app.page === "MAIN") {
      html = (
        <div className={styles.App}>
          <div className={styles.BoardContainer}>
            <Board></Board>
          </div>
          <div className={styles.ControlBarContainer}>
            <ControlBar></ControlBar>
          </div>
        </div>
      )
    }
    else if (this.props.app.page === "LOGIN") {
      html = (
        <div className={styles.App}>
          <div className={styles.LoginContainer}>
            <h1>TEAM Chess</h1>
            <h3>Please Login</h3>
            <input type="text"className={styles.Username}></input>
            <input type="text" className={styles.Password}></input>
            <input type="button" className={styles.Login} value="LOGIN"></input>
          </div>
        </div>
      )
    }
    else if (this.props.app.page === "SIGN_UP") {

    }
    return (
      html
    )
  }

}

//export default App;
//export default App = connect(mapStateToProps)(App);
export default connect(mapStateToProps, mapDispatchToProps)(App);
