import { createStore } from "redux";
import appReducer from "./reducers/reducer";
import { version } from '../../package.json';
import { getPureCloudImplicitGrantClientId, getPureCloudImplicitGrantCallbackUrl, getAppOrigin} from "./constants/general";


const initialState = {
    loggedIn: false,  // redirect
    loginErrorMessage: null, // to show the error message

    pureCloudImplicitGrantClientId: getPureCloudImplicitGrantClientId(), //get client id from config.json
    pureCloudImplicitGrantCallbackUrl: getPureCloudImplicitGrantCallbackUrl(),//get callback url from config.json
    origin : getAppOrigin(),
    implicitGrantCredentialsToken: null, //no token on start
    version: version, //get version to display 
    implicitGrantExpiry: null,
    //queueList: null,
    //queue: null
    conversationsUpdated : false,
    userList: null,
    userId: null,
    userName: null,
    userConversations: null,
}

const store = createStore(appReducer, initialState);

export default store;
